where id_cliente = #{idCliente,jdbcType=INTEGER}
  and id_operador = #{idOperador,jdbcType=INTEGER}

mapper.insert:

insert into t_clientes_operadores (id_cliente, id_operador, impuesto, 
  precio_kg, usu_crea, fecha_crea, 
  usu_modifica, fecha_modifica, estado
  )
values (#{idCliente,jdbcType=INTEGER}, #{idOperador,jdbcType=INTEGER}, #{impuesto,jdbcType=DOUBLE}, 
  #{precioKg,jdbcType=DOUBLE}, #{usuCrea,jdbcType=INTEGER}, #{fechaCrea,jdbcType=TIMESTAMP}, 
  #{usuModifica,jdbcType=INTEGER}, #{fechaModifica,jdbcType=TIMESTAMP}, #{estado,jdbcType=INTEGER}
  )

mapper.update:

update t_clientes_operadores
set impuesto = #{impuesto,jdbcType=DOUBLE},
  precio_kg = #{precioKg,jdbcType=DOUBLE},
  usu_crea = #{usuCrea,jdbcType=INTEGER},
  fecha_crea = #{fechaCrea,jdbcType=TIMESTAMP},
  usu_modifica = #{usuModifica,jdbcType=INTEGER},
  fecha_modifica = #{fechaModifica,jdbcType=TIMESTAMP},
  estado = #{estado,jdbcType=INTEGER}
where id_cliente = #{idCliente,jdbcType=INTEGER}
  and id_operador = #{idOperador,jdbcType=INTEGER}
