mapper.select:

select 'false' as QUERYID,

from t_clientes_vehiculos
where id_cliente = #{idCliente,jdbcType=INTEGER}
  and matricula = #{matricula,jdbcType=VARCHAR}
  and tipo = #{tipo,jdbcType=INTEGER}

mapper.delete:

delete from t_clientes_vehiculos
where id_cliente = #{idCliente,jdbcType=INTEGER}
  and matricula = #{matricula,jdbcType=VARCHAR}
  and tipo = #{tipo,jdbcType=INTEGER}

mapper.update:

update t_clientes_vehiculos


    usu_crea = #{usuCrea,jdbcType=INTEGER},


    fecha_crea = #{fechaCrea,jdbcType=TIMESTAMP},


    usu_modifica = #{usuModifica,jdbcType=INTEGER},


    fecha_modifica = #{fechaModifica,jdbcType=TIMESTAMP},


    estado = #{estado,jdbcType=INTEGER},


where id_cliente = #{idCliente,jdbcType=INTEGER}
  and matricula = #{matricula,jdbcType=VARCHAR}
  and tipo = #{tipo,jdbcType=INTEGER}


update t_clientes_vehiculos
set usu_crea = #{usuCrea,jdbcType=INTEGER},
  fecha_crea = #{fechaCrea,jdbcType=TIMESTAMP},
  usu_modifica = #{usuModifica,jdbcType=INTEGER},
  fecha_modifica = #{fechaModifica,jdbcType=TIMESTAMP},
  estado = #{estado,jdbcType=INTEGER}
where id_cliente = #{idCliente,jdbcType=INTEGER}
  and matricula = #{matricula,jdbcType=VARCHAR}
  and tipo = #{tipo,jdbcType=INTEGER}
